<script>
import { defineComponent } from 'vue';
import { useSettingsStore } from '@/stores/settings'
import { useItemsStore } from '@/stores/items';
import { useImageStore } from '@/stores/image';
import ToolbarComponent from './components/ToolbarComponent.vue';
import TilesGrid from '@/components/tiles/TilesGrid.vue';
import { chromeStorage } from './plugins/chromeStorage';
import { v4 as uuid } from 'uuid';
import FirstLaunch from '@/components/FirstLaunch.vue';

export default defineComponent({
  components: {
    ToolbarComponent,
    TilesGrid,
    FirstLaunch
  },
  data() {
    return {
      showUaModal: false,
      settings: useSettingsStore(),
      items: useItemsStore(),
      images: useImageStore(),
      mockItems: [
        {
          link: {
            "id": uuid(),
            "label": "Come Back Alive",
            "url": "https://savelife.in.ua/en/donate-en/#donate-army-card-monthly",
            "fontColor": "black",
            "bgColor": "#fdda01",
            "size": "m",
          },
          "img": "data:image/jpeg;base64," 
        },
        {
          link: {
            "id": uuid(),
            "label": "UNITED24",
            "url": "https://u24.gov.ua/",
            "bgColor": "black",
            "fontColor": "white",
            "size": "m"
          },
          img: "data:image/jpeg;base64,"
        },
        {
          link: {
            "id": uuid(),
            "label": "Serhiy Prytula Charity Foundation",
            "url": "https://prytulafoundation.org/en",
            "bgColor": "#ffa401",
            "fontColor": "#001f62",
            "size": "m"
          },
          img: "data:image/jpeg;base64,"
        },
      ]
    }
  },
  beforeCreate() {
    Promise.all([
      chromeStorage.get('toolbarPosition').then((value) => {
        this.settings.setToolbarPosition(value ?? 'top');
      }),
      chromeStorage.get('gridWidth').then((value) => {
        this.settings.setGridWidth(value ?? '85');
      }),
      chromeStorage.get('gridGap').then((value) => {
        this.settings.setGridGap(value ?? 10);
      }),
      chromeStorage.get('tileCornerRadius').then((value) => {
        this.settings.setTileCornerRadius(value ?? '10');
      }),
      chromeStorage.get('tileFaviconSize').then((value) => {
        this.settings.setTileFaviconSize(value ?? '32');
      }),
      chromeStorage.get('tileLabelPosition').then((value) => {
        this.settings.setTileLabelPosition(value ?? 'bottom right');
      }),
      chromeStorage.get('showBookmarksLabel').then((value) => {
        this.settings.setLabelFor('bookmarks', value ?? false);
      }),
      chromeStorage.get('showRecentlyClosedLabel').then((value) => {
        this.settings.setLabelFor('bookmarks', value ?? false);
      }),
      chromeStorage.get('showNewTileLabel').then((value) => {
        this.settings.setLabelFor('bookmarks', value ?? false);
      }),
      chromeStorage.get('showSettingsLabel').then((value) => {
        this.settings.setLabelFor('bookmarks', value ?? false);
      }),
      chromeStorage.getLocalAll().then((res) => {
        for (const [key, value] of Object.entries(res)) {
          this.images.set(key, value);
        }
      })
    ]);

    chromeStorage.get('links').then((value) => {
      if (value) {
        this.items.setItems(JSON.parse(value))
      } else {
        this.showUaModal = true;
        const imgs = this.mockItems.map((item) => {
          return {
            id: item.link.id,
            img: item.img
          }
        })
        imgs.forEach((item) => {
          this.images.set(item.id, item.img)
        })

        this.mockItems.map(item => item.link).forEach((link) => {
          this.items.addItem(link)
        });
      } 
    })
  },
  methods : {
    flexOrientation() {
      return this.settings.toolbarPosition === 'top' ? 'flex-col' : 'flex-col-reverse'
    }
  }
})
</script>



<template>
  <div class="app flex w-100 h-screen" :class="[flexOrientation()]">
    <ToolbarComponent />
    <div class="grow p-6">
      <TilesGrid />
    </div>
  </div>
    <input type="checkbox" id="first-launch" class="modal-toggle" :checked="showUaModal" />
    <div class="modal" role="dialog">
      <FirstLaunch @close="showUaModal = false"/>
    </div>
</template>

